    /**
     * Este evento verifica que solo se ingresen caracteres numericos. Si se
     * preciona una tecla no numerica se mostrara un mensaje en pantalla
     * diciendo que solo se pueden presionar numeros
     *
     * @param evt
     */
    private void op1KeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_op1KeyTyped
        char e = evt.getKeyChar();
        if (Character.isLetter(e)) {
            JOptionPane.showMessageDialog(null, "Solo debes ingresar numeros");
            op1.setText("");
        }
    }//GEN-LAST:event_op1KeyTyped
